sections.css:
@import "base/body"
@import "base/paragraph"
@import "base/heading"
@import "base/blockquote"
@import "base/link"
@import "base/ordered_list"
@import "base/unordered_list"
@import "base/table"
@import "base/radio"
@import "base/checkbox"
@import "base/input"
@import "base/shared"

// Component
// --------------------------------------------------
.radio
  display: block
  margin: 15px 0

// Elements
// --------------------------------------------------
.mdl-radio__inner-circle
  background: $color-blue-medium

// Modifiers
// --------------------------------------------------
.radio--inline
  display: inline-block
  vertical-align: middle
  margin-right: 15px
  &:last-of-type
    margin-right: 0

// Dependiencies
// --------------------------------------------------
.mdl-radio__ripple-container
  .mdl-ripple
    background: $color-blue-medium

// States
// --------------------------------------------------
.mdl-radio.is-checked
  .mdl-radio__outer-circle
    border-color: $color-blue-medium

// Component
// --------------------------------------------------
.checkbox
  display: block
  margin: 15px 0

// Elements
// --------------------------------------------------
.mdl-checkbox__ripple-container
  .mdl-ripple
    background-color: $color-blue-medium

// Modifiers
// --------------------------------------------------
.checkbox--inline
  display: inline-block
  width: auto
  margin-right: 15px
  vertical-align: middle
  &:last-of-type
    margin-right: 0

// Dependiencies
// --------------------------------------------------

// States
// --------------------------------------------------
.mdl-checkbox.is-checked
  .mdl-checkbox__box-outline
    border-color: $color-blue-medium
  .mdl-checkbox__tick-outline
    background-color: $color-blue-medium

@import "components/search-bar"
@import "components/header"
@import "components/header-logo"
@import "components/header-menu"
@import "components/login-menu"
@import "components/icons-bar"
@import "components/sidebar-menu"
@import "components/listing"
@import "components/featured"
@import "components/featured-lg"
@import "components/icon"
@import "components/background"
@import "components/button"
@import "components/form"
@import "components/editor-area"
@import "components/tab"
@import "components/alert"
@import "components/slider"
@import "components/form-table"
@import "components/file-upload"
@import "components/draggable"
@import "components/sidebar-tabs"
@import "components/anonymous-menu"
@import "components/circle-menu"
@import "components/filters"
@import "components/details"
@import "components/action-link"
@import "components/etiquette"
@import "components/timeline"
@import "components/announcement"

// Sections
@import "layouts/sections/section"
@import "layouts/sections/section--header"
@import "layouts/sections/section--content"
@import "layouts/sections/section--featured"

// Layouts
@import "layouts/layout"
@import "layouts/grid"

@import "mdl-hacks"
@import "drupal-hacks"

// Component
// --------------------------------------------------
.button
  font-weight: $font-weight-medium
  line-height: initial
  height: auto
  padding: 15px 30px
  letter-spacing: 2px
  color: #fff
  border-width: 3px
  border-style: solid
  border-color: $color-blue-lighter
  background-color: $color-blue-lighter !important
  +border-radius(4px)

// Elements
// --------------------------------------------------

// Modifiers
// --------------------------------------------------
.button--blue
  color: #fff
  border-color: $color-blue-medium
  background-color: $color-blue-medium !important

.button--transparent
  color: $color-grey-medium !important
  border-color: $color-grey-medium
  background-color: transparent !important

.button--full-width
  width: 100%

// Dependiencies
// --------------------------------------------------

// States
// --------------------------------------------------

// Component
// --------------------------------------------------
.etiquette
  font-size: $font-size-xsmall
  font-weight: $font-weight
  display: inline-block
  padding: 5px 20px
  vertical-align: middle
  +border-radius(15px)
  +breakpoint(mobile)
    margin-left: 10px
    margin-bottom: 0

// Elements
// --------------------------------------------------

// Modifiers
// --------------------------------------------------
.etiquette--blue
  color: #fff
  background-color: lighten($color-blue-default, 15%)

// Dependiencies
// --------------------------------------------------

// Component
// --------------------------------------------------

// Elements
// --------------------------------------------------
.file-upload__wrapper
  padding: 30px
  text-align: center
  border: 2px dashed lighten($color-grey-medium, 20%)
  +border-radius(4px)

.file-upload__heading
  font-weight: $font-weight
  margin-top: 0
  color: darken($color-grey-medium, 10%)

.file-upload__description
  margin-top: 24px
  color: lighten($color-grey-medium, 10%)

// Modifiers
// --------------------------------------------------

// Dependiencies
// --------------------------------------------------

// States
// --------------------------------------------------

